var $ = jQuery;
$(document).ready(() => {
  $("button.plus").click(function() {
    var qt = parseInt($(this).siblings(".quantity").children('input').val());
    $(this).siblings(".quantity").children('input').val(qt+1);
  });
    $("button.minus").click(function() {
    var qt = parseInt($(this).siblings(".quantity").children('input').val());
      if (qt > 1) {
    	$(this).siblings(".quantity").children('input').val(qt-1);    
      }
    
  });
  
  // Links
  $(".woocommerce-LoopProduct-link.woocommerce-loop-product__link").attr('href', 'javascript:void(0)');
  
  // Hide second title
  $(".astra-shop-thumbnail-wrap woocommerce-loop-product__title").remove();
  
  // Weight
  $(".ast-woo-product-category").each(function(i, v) {
    if ($(v).text().indexOf('ירקות') > -1 || $(v).text().indexOf('פירות') > -1) {
      $(v).siblings(".price").append('<span class="priceByWeight"> / ק"ג</span>');
    }
  });
});