var $ = jQuery;

$(document).ready(() => {
  $("#billing_city").keyup(function() {
    setTimeout(() => {
      var val = $(this).val();
      console.log(val);
      var shippingLabel = $("#shipping_method label:contains(" + val + ")");

      if (shippingLabel.length) {
        shippingLabel.siblings('input').attr("checked", "checked");
      }
      }, 2000);

  });
  
});